CREATE TABLE IF NOT EXISTS ncm_genero (
	codigo int4 NOT NULL,
	descricao varchar NULL,
	dt_ini date NOT NULL,
	dt_fim date NULL,
	CONSTRAINT ncm_genero_pk PRIMARY KEY (codigo),
	CONSTRAINT ncm_genero_codigo UNIQUE (codigo, dt_ini)
);


CREATE TABLE IF NOT EXISTS ncm (
	codigo int4 NOT NULL,
	descricao varchar NULL,
	genero int4 NOT NULL REFERENCES ncm_genero(codigo),
	codigo_txt varchar NULL,
	CONSTRAINT ncm_pk PRIMARY KEY (codigo)
);

